INSERT INTO CNBS_DEV.dbo.Componente VALUES ('Actividades de Control', '5', 1, 0, 2017);
    INSERT INTO CNBS_DEV.dbo.Elemento VALUES ('Archivo Institucional', '5.1', 5, 1, 0, 2017);
        INSERT INTO CNBS_DEV.dbo.Practica VALUES ('La máxima autoridad debe considerar que los sistemas de registro e información sean computarizados, manuales o una mezcla, siempre que cumplan con las siguientes características: a) Los programas informáticos, de preferencia, deben ser integrados evitando la duplicidad de registros.', '5.1.1', '2008-11-11', '2008-11-05', 'Dario', 9, 1, 0, 2017);
            INSERT INTO CNBS_DEV.dbo.Actividad VALUES ('Evaluar anualmente el funcionamiento, utilidad, contenido y vigencia de los sistemas de información implementados en su dependencia y gestionar su rediseño o eliminación de los mismos.', '5.1.1.1', '2008-11-11', 'Dario', 'dario.villalta@gmail.com', 14, 1, 1, 1, 1, 1, 0, 2017);
